function scrollFunction(){if(document.body.scrollTop>50||document.documentElement.scrollTop>50){var n=document.getElementById("top-navbar");n.classList.remove("navbar-transparent"),n.classList.add("navbar-transparent-dark")}else{n=document.getElementById("top-navbar");n.classList.remove("navbar-transparent-dark"),n.classList.add("navbar-transparent")}}window.onscroll=function(){scrollFunction()},window.onload=function(){document.querySelectorAll(".nav-link").forEach(n=>{n.href===window.location.href&&(n.setAttribute("aria-current","page"),n.setAttribute("class","nav-link active"))})};